#include <string>#include <vector>#include <queue>using namespace std;vector<int> solution(vector<int> array, vector<vector<int>> commands) {    vector<int> answer;        for(auto cmd : commands){        int i = cmd[0] - 1, j = cmd[1] - 1, k = cmd[2];        priority_queue<int>pq;                while(pq.size() < k){            pq.push(array[i++]);        }                while(i<=j){            pq.push(array[i++]);            pq.pop();        }        answer.push_back(pq.top());    }        return answer;}